sys_script_include.script:
var s_NeedItUtils = Class.create();
s_NeedItUtils.prototype = {
    initialize: function() {
    },
isDatePast: function(strDate){
    // Create GlideDateTime objects for the current date and the passed in date
    var rightnow = new GlideDateTime();
    var testdate = new GlideDateTime(strDate);

    // If the testdate is before rightnow, return true, else return false
    if (testdate.before(rightnow)) {
      return true;
    }
    else {
      return false;
    }
  },

//   var oggi=rightnow.getDate();
//   var prog=whenNeeded.getDate();
//   if(oggi.equals(prog))

isDateOggi: function(strDate){
    // Create GlideDateTime objects for the current date and the passed in date
    var rightnow = new GlideDateTime();
    var testdate = new GlideDateTime(strDate);
    var oggi=rightnow.getDate();
	var prog=testdate.getDate();

    if (oggi.equals(prog)) {
      return true;
    }
    else {
      return false;
    }
  },

    type: 's_NeedItUtils'
};
